CREATE TABLE diner.foods_version(
 id_food_version UUID PRIMARY KEY DEFAULT gen_random_uuid(),
 xid_food UUID REFERENCES diner.foods(id_food) ON DELETE CASCADE NOT NULL,
 name VARCHAR(30) NOT NULL,
 description TEXT,
 price NUMERIC(10,2) NOT NULL,
 promotion NUMERIC(10,2),
 stock BOOLEAN DEFAULT TRUE NOT NULL,
 sale BOOLEAN DEFAULT TRUE NOT NULL,
 created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);


